import ReactDOM from 'react-dom'
import React, { useState } from 'react';
import axios from 'axios';

class App extends React.Component {
	render() {
  	return (
    	<div>
      <Form />
    	</div>
    );
  }	
}

class Form extends React.Component {
  state = { NTID: '', pwd: ''};

  handleSubmit = async (event) => {
    event.preventDefault();
    const usr = await axios.get(`https://api.github.com/users/${this.state.NTID}`);
    const pass = await axios.get(`https://api.github.com/users/${this.state.pwd}`);
    this.props.onSubmit(usr.data);
    this.props.onSubmit(pass.data);
    this.setState({ NTID: '', pwd: '' });
  };

	render() {
  	return (
    	<form onSubmit={this.handleSubmit}>
      <span className="formtext">&#x3C;Form /&#x3E;</span>
    	  <input 
          type="text" 
          value={this.state.NTID}
          onChange={event => this.setState({ NTID: event.target.value})}
          placeholder="NTID" 
          required 
        /> <br/>

        <input 
          type="text" 
          value={this.state.pwd}
          onChange={event => this.setState({ pwd: event.target.value})}
          placeholder="Password" 
          required 
        />
        <button>Login</button>
    	</form>
    );
  }
}

export default App;
