import React from 'react';
import axios from 'axios';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useHistory
} from "react-router-dom";

class App extends React.Component {
	render() {
  	return (
    	<div>
        <Router>
        <Switch>
          <Route path="/" component={Form} />
          <Route path="/usertype" component={UserType}>
            <UserType />
          </Route>
        </Switch>
        </Router>
    	</div>
    );
  }	
}

class Form extends React.Component {
  state = { name: '', password: '', show_error: false};

  handleSubmit = async (event) => {
    event.preventDefault();
    const resp = await axios.post('http://localhost:5000/login', {
      name : this.state.name,
      password : this.state.password
    });
    this.setState({ name: '', password: '' });

    if (resp.data.auth) {
      this.setState({show_error: false})
      this.props.history.push(`/usertype`)
    }
    else {
      this.setState({show_error: true})
    }
  };

	render() {
  	return (
    	<form onSubmit={this.handleSubmit}>
      <span className="formtext">&#x3C;Form /&#x3E;</span>
    	  <input 
          type="text" 
          value={this.state.name}
          onChange={event => this.setState({ name: event.target.value})}
          placeholder="NTID" 
          required 
        /> <br/>

        <input 
          type="password" 
          value={this.state.password}
          onChange={event => this.setState({ password: event.target.value})}
          placeholder="Password" 
          required 
        />
        <button>Login</button>
        {this.state.show_error ? <p>User not found!</p>:null}
    	</form>
    );
  }
}

class UserType extends React.Component {
  render() { 
    return(<p>test</p>)
  }
}

export default App;
